import React from 'react';
// Importamos 'useNavigate' para permitir a navegação programática
import { useNavigate } from 'react-router-dom'; 
import './TelaInicial.css';

function TelaInicial() {
  // Inicializa o hook de navegação (obrigatório para fazer o redirecionamento)
  const navigate = useNavigate(); 

  // Define a função que será chamada no clique do botão
  const handleStartExploring = () => {
    // Redireciona o usuário para a rota definida no App.js
    // Mude '/MenuPrincipal' para a rota de destino real, se for diferente.
    navigate('/MenuPrincipal'); 
  };

  return (
    <div className="tela-container">
      <video autoPlay loop muted className="video-fundo">
        {/* O caminho do src é relativo à pasta public (raiz do site) */}
        <source src="/social_u57581.mp4" type="video/mp4" />
        Seu navegador não suporta vídeo.
      </video>

      <div className="conteudo">
        <div className="containerLogo">
          <img src="/Astrovision_1.svg" alt="Logo"/> 
        </div>

        <p>
          You're about to start a journey through the stars! 🌟<br />
          Pilot your ship, search for new worlds, and learn amazing facts about exoplanets.
        </p>

        <button className="btn-explorar" onClick={handleStartExploring}>
          Start exploring
        </button>
      </div>
    </div>
  );
}

export default TelaInicial;
