import React, { useState, useEffect, useCallback } from 'react';

// --- Dados dos Planetas (Coordenadas Fixas) ---
const PLANETS = [
  // Posi칞칫es em pixels (x, y) relativas ao canto superior esquerdo da tela
  { id: 'kepler', name: 'Kepler-164Bc', x: 250, y: 150, color: 'purple' },
  { id: 'zylos', name: 'Zylos-Prime', x: 800, y: 500, color: 'orange' },
];

const SPEED = 10;          // Pixels por passo de movimento
const PROXIMITY_RADIUS = 100; // Raio em pixels para habilitar o pouso

// ---------------------------------------------
// Componente de Navegacao Principal (SpaceExplorer)
// ---------------------------------------------

function TelaNavegacao() {
  const [rocketPos, setRocketPos] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });
  const [closePlanet, setClosePlanet] = useState(null); // Planeta mais pr칩ximo
  const [currentView, setCurrentView] = useState('space'); // 'space' ou 'planetDetail'
  const [landedPlanet, setLandedPlanet] = useState(null); // Planeta onde o foguete aterrissou

  // --- L칩gica de Checagem de Proximidade ---
  const checkProximity = useCallback((rocketX, rocketY) => {
    for (const planet of PLANETS) {
      const distance = Math.sqrt(
        Math.pow(planet.x - rocketX, 2) + Math.pow(planet.y - rocketY, 2)
      );
      if (distance < PROXIMITY_RADIUS) {
        return planet;
      }
    }
    return null;
  }, []);

  // --- L칩gica de Controle do Teclado ---
  const handleKeyDown = useCallback((event) => {

    // Se estiver na tela de detalhes, s칩 permite voltar
    if (currentView === 'planetDetail') {
      if (event.key === 'Escape' || event.key === 'r') {
        setCurrentView('space');
        setLandedPlanet(null);
        setClosePlanet(null);
      }
      return;
    }

    // L칩gica de movimento e pouso (apenas na vista 'space')
    setRocketPos(currentPos => {
      let newX = currentPos.x;
      let newY = currentPos.y;

      switch (event.key.toLowerCase()) {
        case 'w':
        case 'arrowup': newY -= SPEED; break;
        case 's':
        case 'arrowdown': newY += SPEED; break;
        case 'a':
        case 'arrowleft': newX -= SPEED; break;
        case 'd':
        case 'arrowright': newX += SPEED; break;

        case 'e': // Tenta pousar
          const planetToLand = checkProximity(newX, newY);
          if (planetToLand) {
            setLandedPlanet(planetToLand);
            setCurrentView('planetDetail');
          }
          break;
        default:
          return currentPos;
      }

      // Atualiza o estado de proximidade ap칩s o movimento
      setClosePlanet(checkProximity(newX, newY));

      // Retorna a nova posi칞칚o
      return { x: newX, y: newY };
    });
  }, [currentView, checkProximity]);

  useEffect(() => {
    // Adiciona o listener quando o componente monta
    window.addEventListener('keydown', handleKeyDown);

    // Remove o listener quando o componente desmonta
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);


  // --- Renderiza칞칚o Condicional ---
  if (currentView === 'planetDetail' && landedPlanet) {
    return <PlanetDetailScreen planet={landedPlanet} />;
  }

  // Renderiza칞칚o do Espa칞o Sideral
  return (
    // tabIndex={0} torna o div focaliz치vel para receber eventos de teclado
    <div className="space-container" tabIndex={0}>
      <Rocket x={rocketPos.x} y={rocketPos.y} />

      {PLANETS.map(planet => (
        <Planet
          key={planet.id}
          planet={planet}
          isClose={closePlanet && closePlanet.id === planet.id}
        />
      ))}

      <Controls isClose={!!closePlanet} />
    </div>
  );
}

// ---------------------------------------------
// Componentes de Apresenta칞칚o (Styling)
// ---------------------------------------------

// Placeholder para a tela de detalhes do planeta
const PlanetDetailScreen = ({ planet }) => (
  <div className="detail-screen">
    <h1>Bem-vindo a {planet.name}</h1>
    <p>Detalhes sobre a superf칤cie e atmosfera...</p>
    <p className="hint">Pressione ESC ou 'R' para retornar ao espa칞o.</p>
  </div>
);

// Componente do Foguete
const Rocket = ({ x, y }) => (
  <div 
    className="rocket"
    style={{ left: `${x}px`, top: `${y}px` }}
  >
     {/* 칈cone ou imagem do foguete */}
     游
  </div>
);

// Componente do Planeta
const Planet = ({ planet, isClose }) => (
  <div 
    className="planet"
    style={{ left: `${planet.x}px`, top: `${planet.y}px`, backgroundColor: planet.color }}
  >
    {isClose && (
      <div className="tooltip">
        {planet.name}
        <br/>
        <span className="tooltip-action">Press E to land</span>
      </div>
    )}
  </div>
);

// Componente de Controles
const Controls = ({ isClose }) => (
    <div className="controls">
        <p>Controls:</p>
        <ul>
            <li>Use W/A/S/D or Arrow keys to move</li>
            {isClose && <li>Press E to land on a planet</li>}
            {!isClose && <li>Move close to a planet to land</li>}
        </ul>
    </div>
);

export default TelaNavegacao;
