import React, { useState, useEffect, useCallback } from 'react';
const SPEED = 5;
import './TelaNavegacao.css'


function TelaNavegacao() {
    <img src="./fundo-espaco-noturno.jpg" alt="Logo"/>
    const [rocketPos, setRocketPos] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });

  const handleKeyDown = useCallback((event) => {
    setRocketPos(currentPos => {
      let newX = currentPos.x;
      let newY = currentPos.y;

      // Movimento W/A/S/D ou Setas
      switch (event.key) {
        case 'w':
        case 'ArrowUp':
          newY -= SPEED;
          break;
        case 's':
        case 'ArrowDown':
          newY += SPEED;
          break;
        case 'a':
        case 'ArrowLeft':
          newX -= SPEED;
          break;
        case 'd':
        case 'ArrowRight':
          newX += SPEED;
          break;
        // Lógica para 'E' (aterrissar) viria aqui, checando a proximidade com um planeta
        default:
          return currentPos; // Não muda a posição para outras teclas
      }

      // Adicionar lógica de limites da tela para newX e newY

      return { x: newX, y: newY };
    });
  }, []);

  useEffect(() => {
    // Adiciona o listener quando o componente monta
    window.addEventListener('keydown', handleKeyDown);

    // Remove o listener quando o componente desmonta
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyDown]);

  return (
    <div className="space-container" tabIndex={0}>
      {/* O style usa a posição do estado */}
      <Rocket
        x={rocketPos.x}
        y={rocketPos.y}
      />
      {/* Renderização dos planetas com suas posições fixas */}
      {/* ... */}
    </div>
  );
}

// Componente Rocket simples
const Rocket = ({ x, y }) => (
  <img
    src="/Nave_basica.png" // Substitua pelo caminho da sua imagem
    className="rocket"
    style={{ left: `${x}px`, top: `${y}px` }}
    alt="Foguete"
  />
);

export default TelaNavegacao
