import React, { useState, useEffect, useCallback } from "react";
import "./TelaNavegacao.css";
import Modal from "./Modal";
import ModalDois from "./ModalDois";

const PLANETS = [
  { 
    id: 1, 
    name: "Nebula Prime", 
    src: "/planetavermelho.png", 
    relativeX: 100, 
    relativeY: 150 
  }, 
  { 
    id: 2, 
    name: "Aetheria", // planetaclaro
    src: "/planetaclaro.png", 
    relativeX: 850, 
    relativeY: 800 
  },
];

const PROXIMITY_RADIUS = 120; 
const ROCKET_SIZE = 100;

const ReturnButton = ({ onClick }) => (
  <button className="returnButton" onClick={onClick}>
      Return to mission
  </button>
);

function TelaNavegacao() {
  const [rocketPos, setRocketPos] = useState({
    x: window.innerWidth / 2 - ROCKET_SIZE / 2,
    y: window.innerHeight / 2 - ROCKET_SIZE / 2,
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState({});
  const [isModalDoisOpen, setIsModalDoisOpen] = useState(false);
  const [planetPositions, setPlanetPositions] = useState([]);

  const calculatePlanetPositions = useCallback(() => {
    const width = window.innerWidth;
    const height = window.innerHeight;

    const newPositions = PLANETS.map(planet => {
      const x = (planet.relativeX / 1000) * width;
      const y = (planet.relativeY / 1000) * height;
      return {
        ...planet,
        x: x - ROCKET_SIZE / 2, 
        y: y - ROCKET_SIZE / 2,
      };
    });

    setPlanetPositions(newPositions);
  }, []);

  const handleMouseMove = useCallback((event) => {
    setRocketPos({
      x: event.clientX - ROCKET_SIZE / 2,
      y: event.clientY - ROCKET_SIZE / 2,
    });
  }, []);

  const checkProximity = useCallback(() => {
    const rocketCenterX = rocketPos.x + ROCKET_SIZE / 2;
    const rocketCenterY = rocketPos.y + ROCKET_SIZE / 2;

    for (const planet of planetPositions) {
      const planetCenterX = planet.x + ROCKET_SIZE / 2;
      const planetCenterY = planet.y + ROCKET_SIZE / 2;

      const distance = Math.sqrt(
        Math.pow(planetCenterX - rocketCenterX, 2) + Math.pow(planetCenterY - rocketCenterY, 2)
      );

      if (distance < PROXIMITY_RADIUS) {
        return planet;
      }
    }

    return null;
  }, [rocketPos, planetPositions]);

  const openModalDois = (planet) => {
    if (planet.name === "Aetheria") {
      setIsModalDoisOpen(true);
    }
  };

  const handleSpaceClick = useCallback((event) => {
    if (event.target.className.includes("return-button") || event.target.className.includes("logo")) {
      return;
    }

    const closePlanet = checkProximity();
    if (closePlanet) {
      if (closePlanet.name === "Aetheria") {
        openModalDois(closePlanet);
      } else {
        setModalContent({
          title: `Navegando para ${closePlanet.name}`,
          body: `Você chegou ao planeta ${closePlanet.name}! Pronto para a aventura?`,
        });
        setIsModalOpen(true);
      }
    }
  }, [checkProximity]);

  const closeModal = () => setIsModalOpen(false);
  const closeModalDois = () => setIsModalDoisOpen(false);

  const handleReturn = () => {
    alert("Retornando para a tela de Missão!");
  };

  useEffect(() => {
    calculatePlanetPositions();
    window.addEventListener("resize", calculatePlanetPositions);
    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("click", handleSpaceClick);

    return () => {
      window.removeEventListener("resize", calculatePlanetPositions);
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("click", handleSpaceClick);
    };
  }, [calculatePlanetPositions, handleMouseMove, handleSpaceClick]);

  return (
    <div className="space-container">
      <img src="/fundo-espaco-noturno.jpg" alt="Fundo do espaço" className="space-background" />

      <div className="top-ui">
        <img src="/logo2 (1).svg" alt="Logo" className="logo" />
        <ReturnButton onClick={handleReturn} />
      </div>

      {planetPositions.map((planet) => (
        <Planet 
          key={planet.id}
          src={planet.src} 
          alt={`Planeta ${planet.name}`}
          x={planet.x}
          y={planet.y}
        />
      ))}

      <Rocket x={rocketPos.x} y={rocketPos.y} size={ROCKET_SIZE} />

      <Modal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        title={modalContent.title}
      >
        <p>{modalContent.body}</p>
      </Modal>

      <ModalDois 
        isOpen={isModalDoisOpen} 
        onClose={closeModalDois} 
      />
    </div>
  );
}

const Planet = ({ src, alt, x, y }) => (
  <img
    src={src}
    className="planet"
    style={{ left: `${x}px`, top: `${y}px` }}
    alt={alt}
  />
);

const Rocket = ({ x, y, size }) => (
  <img
    src="/Nave_basica.png"
    className="rocket"
    style={{ left: `${x}px`, top: `${y}px`, width: `${size}px`, height: `${size}px` }}
    alt="Foguete"
  />
);

export default TelaNavegacao;
