import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './TelaAvancada.css'; // Onde colocaremos o CSS de estilo

// Componente para o Card (mantido simples, ajustado para o layout)
const Card = ({ title, description, isSelected = false, onClick }) => (
  <div 
    className={`card ${isSelected ? 'selected' : ''}`}
    onClick={onClick}
  >
    {/* Conte√∫do do Card, como na imagem */}
    <div className="cardzinho">
      <p className="step-title">Step 1 of 2</p>
      <div className="upload-section">
        <span className="uploadzinho">üîç</span>
        <h2 className="cardTitle">{title}</h2>
      </div>
      <p className="Descricaozinha">{description}</p>
    </div>

    <div className="upload-area">
      {/* √Årea do upload (com a borda pontilhada) */}
      <span className="uploadzinhooo">üìÑ</span>
      <p>Upload & analyze</p>
    </div>
  </div>
);

// Componente de navega√ß√£o (Return to Missions)
const ReturnButton = () => {
    const navigate = useNavigate();
    // Substitua '/MenuPrincipal' pela rota correta de miss√µes se for diferente
    return (
        <button 
            className="botaozinho" 
            onClick={() => navigate('/MenuPrincipal')} 
            title="Return to Missions"
        >
            ‚Üê Return to Missions
        </button>
    );
};

function TelaAvancada() {
  const navigate = useNavigate();
  const [selectedCard, setSelectedCard] = useState(null);

  // A rota de destino deve ser o pr√≥ximo passo do seu fluxo
  const handleCardClick = (cardTitle) => {
    setSelectedCard(cardTitle);

    // Supondo que o pr√≥ximo passo seja a pr√≥pria TelaAvancada, mas com outra visualiza√ß√£o.
    // Se for uma rota diferente, ajuste o caminho:
    // navigate('/proximo-passo'); 
    alert('Upload data clicked! Implementar aqui o pr√≥ximo passo.');
  };

  return (
    <div className="fundaoSpace">
      {/* TOPO: Logo e Bot√£o de Retorno */}
      <div className="tituluzinho">
        {/* Usando a logo como texto simples ou componentizado, ajuste conforme seu caso */}
        <h1 className="logo-title">Astrovision.ai</h1> 
        <ReturnButton />
      </div>

      {/* ROCKET IMAGEM E ESTRELAS NO FUNDO */}
      <img src="/rocket-image.png" alt="Rocket" className="rocketzinho" />

      {/* CARD DE UPLOAD */}
      <div className="cards-container-wrapper">
        <Card
          title="Upload your data set"
          description="Upload a CSV file with star or light-curve data and let our AI co-pilot analyze it. Your ship will jump to the detected location in space, where the system will identify and classify the object as planet or not."
          isSelected={selectedCard === "Upload"}
          onClick={() => handleCardClick("Upload")}
        />
      </div>
    </div>
  );
}

export default TelaAvancada;
