import React, { useState, useEffect, useCallback } from 'react';
import './TelaNavegacao.css';

const SPEED = 10; // velocidade da nave

function TelaNavegacao() {
  const [rocketPos, setRocketPos] = useState({
    x: window.innerWidth / 2 - 50,
    y: window.innerHeight / 2 - 50,
  });

  const handleKeyDown = useCallback((event) => {
    setRocketPos((currentPos) => {
      let newX = currentPos.x;
      let newY = currentPos.y;

      switch (event.key) {
        case 'w':
        case 'ArrowUp':
          newY -= SPEED;
          break;
        case 's':
        case 'ArrowDown':
          newY += SPEED;
          break;
        case 'a':
        case 'ArrowLeft':
          newX -= SPEED;
          break;
        case 'd':
        case 'ArrowRight':
          newX += SPEED;
          break;
        default:
          return currentPos;
      }

      // Limites da tela
      const rocketWidth = 100;
      const rocketHeight = 100;
      const maxX = window.innerWidth - rocketWidth;
      const maxY = window.innerHeight - rocketHeight;

      if (newX < 0) newX = 0;
      if (newY < 0) newY = 0;
      if (newX > maxX) newX = maxX;
      if (newY > maxY) newY = maxY;

      return { x: newX, y: newY };
    });
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  return (
    <div className="space-container">
      {/* Fundo do espaço */}
      <img src="/fundo-espaco-noturno.jpg" alt="Fundo do espaço" className="space-background" />

      {/* Planetas fixos */}
      <img src="/planetavermelho.png" alt="Planeta 1" className="planet planet1" />
      <img src="/planetaclaro.png" alt="Planeta 2" className="planet planet2" />

      {/* Nave controlável */}
      <Rocket x={rocketPos.x} y={rocketPos.y} />
    </div>
  );
}

const Rocket = ({ x, y }) => (
  <img
    src="/Nave_basica.png"
    className="rocket"
    style={{ left: `${x}px`, top: `${y}px` }}
    alt="Foguete"
  />
);

export default TelaNavegacao;
