import React from 'react';
import './PlanoCards.css';

const Card = ({ title, tag, description, imageUrl, isHighlight = false }) => (
  <div className={`card ${isHighlight ? 'highlight' : ''}`}>
    <div className="card-header">
      <h2 className="card-title">{title}</h2>
      <span className={`tag ${tag.toLowerCase().replace(' ', '-')}`}>{tag}</span>
    </div>
    <p className="card-description">{description}</p>
    <div className="card-image-container">
      <img src={imageUrl} alt={`Ilustração do plano ${title}`} className="card-image" />
    </div>
    <button className="card-button">Escolher Plano</button>
  </div>
);

// Componente principal que contém os dois cartões
function MenuPrincipal() {
  return (
    <div className="planos-container">
      <Card
        title="Free Explorer"
        tag="Basic"
        description="Voe com sua nave para onde quiser. Descubra planetas, pouse e aprenda fatos interessantes. Perfeito para exploradores curiosos!"
        // Substitua pelo caminho real da sua imagem
        imageUrl="/images/foguete_basic.png" 
      />

      <Card
        title="Galactic Pro"
        tag="Premium"
        description="Acesso a todas as rotas secretas, missões exclusivas, modelos 3D de planetas e um diário de bordo avançado. A aventura te espera!"
        imageUrl="/images/foguete_pro.png"
        isHighlight={true} // Opcional: para dar um destaque visual
      />
    </div>
  );
}

export default MenuPrincipal;
